import React, { useState } from 'react';
import FilterCard from './FilterCard'
import FilterModal from './FilterModal'
import './Filter.css'

const filtersInfoMap = {
  'c-filter'      : { label: 'C', type: 'lang' },
  'c++-filter'    : { label: 'C++', type: 'lang' },
  'java-filter'   : { label: 'Java', type: 'lang' },
  'python-filter' : { label: 'Python', type: 'lang' },
  'react-filter'  : { label: 'React', type: 'framework' },
  'nodejs-filter' : { label: 'NodeJS', type: 'framework' },
  'net-filter'    : { label: '.NET Remoting', type: 'framework' },
  'ros-filter'    : { label: 'ROS', type: 'framework' },
  'xamarin-filter': { label: 'Xamarin', type: 'framework' },
  'vdm++-filter'  : { label: 'VDM++', type: 'framework' },
  '2021-filter'   : { label: '2021', type: 'date' },
  '2020-filter'   : { label: '2020', type: 'date' },
  '2019-filter'   : { label: '2019', type: 'date' },
  '2018-filter'   : { label: '2018', type: 'date' },
  '2017-filter'   : { label: '2017', type: 'date' },
  'ml-filter'     : { label: 'Machine Learning', type: 'type' },
  'webapp-filter' : { label: 'Web App', type: 'type' },
  'mobile-filter' : { label: 'Mobile', type: 'type' },
  'game-filter' : { label: 'Game', type: 'type' }
}

const filtersTypesMap = {
  LANGUAGES : ['c-filter', 'c++-filter', 'java-filter', 'python-filter'],
  FRAMEWORKS: ['react-filter', 'nodejs-filter', 'net-filter', 'ros-filter', 'xamarin-filter', 'vdm++-filter'],
  DATES     : ['2021-filter', '2020-filter', '2019-filter', '2018-filter', '2017-filter'],
  TYPES     : ['ml-filter', 'webapp-filter', 'mobile-filter']
};

function Filter() {
  const [ activeFilters, setActiveFilters ] = useState([])
  const [ modalVisibility, setModalVisibility ] = useState(false);

  const updateModalVisibility = () => setModalVisibility(!modalVisibility);
  const updateFilters = (filters) => setActiveFilters([...filters]);
  const handleRemoval = (filters) => {
    let arr = activeFilters;
    setActiveFilters([...arr.filter(id => !filters.includes(id))]);
  }

  return (
    <>
      <section id='filterSection'>
        <p><strong>Filters:</strong></p>
        <div id='filtersHolder'>
          {activeFilters.map(filterId => (
            <FilterCard 
              type='FILTER' 
              key={`${filterId}-card`} 
              id={`${filterId}-card`}
              label={filtersInfoMap[filterId].label}
              onNewFilter={updateModalVisibility} 
              onRemoval={handleRemoval}
            />
          ))}
          <FilterCard 
            type='ADD' 
            key='Add' 
            id='Add' 
            onNewFilter={updateModalVisibility} 
            onRemoval={handleRemoval}
          />
        </div>
        <FilterModal
          activeFilters={activeFilters}
          filtersInfo={filtersInfoMap}
          filtersTypes={filtersTypesMap}
          visible={modalVisibility}
          onClose={updateModalVisibility}
          onSubmit={updateFilters}
        />
      </section>
    </>
  );
}

export default Filter;
