    ['c++-filter', { label: 'C++' }],
    ['java-filter', { label: 'Java' }],
    ['python-filter', { label: 'Python' }]
  ]),
  FRAMEWORKS: new Map([
    ['react-filter', { label: 'React' }],
    ['nodejs-filter', { label: 'NodeJS' }],
    ['net-filter', { label: '.NET Remoting' }],
    ['ros-filter', { label: 'ROS' }],
    ['xamarin-filter', { label: 'Xamarin' }],
    ['vdm++-filter', { label: 'VDM++' }]
  ]),
  DATES: new Map([
    ['2020-filter', { label: '2020' }]
  ]),
  TYPES: new Map([
    ['ml-filter', { label: 'Machine Learning' }]
  ])
};

{ id: 'ml-filter', label: 'Machine Learning' } ,
    { id: 'webapp-filter', label: 'Web App' },
    { id: 'mobile-filter', label: 'Mobile' },
    { id: 'game-filter', label: 'Game' }
*/

function Filter() {
  const [ activeFilters, setActiveFilters ] = useState([])
  const [ modalVisibility, setModalVisibility ] = useState(false);

  const updateModalVisibility = () => setModalVisibility(!modalVisibility);

  const handleRemoval = (filter) => {
    let arr = activeFilters;
    arr = arr.filter(item => item.id !== filter.id)
    setActiveFilters([...arr]);
  }

  const handleAddition = (filter) => {
    let arr = activeFilters;
    arr.push(filter);
    setActiveFilters([...arr]);
  }

  const submit = () => {}

  return (
    <FiltersContext.Provider 
      value={{ 
        activeFilters: activeFilters,
        removeFilters: handleRemoval, 
        addFilters: handleAddition
      }}
    >
      <section id='filterSection'>
        <p>Filters:</p>
        <div id='filtersHolder'>
          <FilterCard 
            type='ADD' 
            key='Add' 
            id='Add' 
            onNewFilter={updateModalVisibility} 
            onRemoval={handleRemoval}/>
          {activeFilters.map(filterId => (
            <FilterCard 
              type='FILTER' 
              key={`${filterId}-card`} 
              id={`${filterId}-card`} 
              onNewFilter={updateModalVisibility} 
              onRemoval={handleRemoval}
            />
          ))}
        </div>
        <FilterModal
          filters={filtersMap}
          visible={modalVisibility}
          onClose={updateModalVisibility}
          onSubmit={submit}
        />
      </section>
    </FiltersContext.Provider>
  );
}

export default Filter;
