import './FilterModal.css'

const languagesFilters = [
  {label: 'C'},
  {label: 'C++'},
  {label: 'Java'},
  {label: 'Python'},
];

const frameworksFilters = [
  {label: 'React'},
  {label: 'NodeJS'},
  {label: '.NET Remoting'},
  {label: 'ROS'},
  {label: 'Xamarin'},
  {label: 'VDM++'},
];

const dateFilters = [
  {label: '2021'},
  {label: '2020'},
  {label: '2019'},
  {label: '2018'},
  {label: '2017'}
];

const typeFilters = [
  {label: 'Machine Learning'},
  {label: 'Web App'},
  {label: 'Mobile'},
  {label: 'Game'},
];

function FilterModal(props) {
  const applyAndClose = (e) => {
    props.onSubmit(['ah']);
    props.onClose();
  }

  const onClickOutside = (e) => {
    if(e.target.classList.contains('modalContainer'))
      props.onClose();
  }

  const filterConstruction = (filter) => (
    <div className='checkboxContainer' key={`${filter.label}-checkbox`}>
      <label className='checkboxLabel' key={`${filter.lab}-label`}>{filter.label}</label>
      <input type='checkbox'/>
    </div>);

    /*checked={false}*/

  return (
    <>
      {props.visible ? 
        <section id='filterModal' className='fullCover'>
          <section className='modalContainer fullCover' onClick={(e) => onClickOutside(e)}>
            <section className='modalBox'>
              <div className='modalTitle'>
                <h3>Filter Selection</h3>
                <div className='closeIcon' onClick={props.onClose}>
                  <i className="fas fa-times"></i>
                </div>
              </div>
              <div className='modalSelection'>
                <div className='filterSelectionGroup'>
                  <h5>Type</h5>
                  {typeFilters.map(filter => filterConstruction(filter))}
                </div>
                <div className='filterSelectionGroup'>
                  <h5>Languages</h5>
                  {languagesFilters.map(filter => filterConstruction(filter))}
                </div>
                <div className='filterSelectionGroup'>
                  <h5>Frameworks</h5>
                  {frameworksFilters.map(filter => filterConstruction(filter))}
                </div>
                <div className='filterSelectionGroup'>
                  <h5>Date</h5>
                  {dateFilters.map(filter => filterConstruction(filter))}
                </div>
              </div>
              <div className='modalConfirmation'>
                <button className='modalCancelBtn' onClick={props.onClose}>Cancel</button>
                <button type='submit' className='modalApplyBtn' onClick={(e) => applyAndClose(e)}>Apply</button>
              </div>
            </section>
          </section>
        </section>
        :
        null}
    </>
  )
}

export default FilterModal;
