import { useState, useLayoutEffect, useRef } from 'react';
import './Quote.css';

function Quote() {
  let cardRef = useRef(null);
  const [ visibility, setVisibility ] = useState(false);

  useLayoutEffect(() => {
    const handleScroll = () => {
      if(cardRef.current.getBoundingClientRect().top < 600) {
        setVisibility(true);
      }
    }
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  });

  return (
    <section id='quote-section' className={`spaced-cover section-content ${visibility ? 'visible-section' : 'hidden-section'}`}>
      <article ref={cardRef}>
        <h1 className='section-header'>Personal Statement</h1>
        <div className='quote-container'>
          <p>Hi there, thank you for visiting my website. Hope you are doing good! My name is Francisco and I am a Software Developer.</p>
          <p>I have recently finished my Master's Degree in Informatics and Computing Engineering from MIEIC, FEUP. Throughout the academic journey I had the opportunity to get in touch with different technologies </p>
        </div>
        <hgroup>
          <h1><i>"Difficulties mastered</i></h1>
          <h1><i>are opportunities won"</i></h1>
          <h5>- Winston Churchill</h5>
        </hgroup>
      </article>
    </section>
  );
}

export default Quote;
