import { useState } from 'react';
import FiltersContext from './FilterContext';
import './FilterModal.css'

function FilterModal(props) {
  const filtersMap = props.filters;
  const [activeFilters, setActiveFilters] = useState([])

  const applyAndClose = () => {
    props.onSubmit(activeFilters);
    props.onClose();
  }

  const onClickOutsideModal = (e) => {
    if(e.target.classList.contains('modalContainer'))
      props.onClose();
  }

  const onCheckboxSelectionChange = (e) => {
    const filterId = e.target.id;
    let arr = activeFilters;

    if (e.target.checked) {
      arr.push(filterId) 
    } else {
      arr = arr.filter(item => item !== filterId);
    }
    setActiveFilters([...arr]);
  } 

  const filterConstruction = (filter) => (
    <div className='checkboxContainer' key={`${filter.id}-checkbox`}>
      <label className='checkboxLabel' key={`${filter.id}-label`}>{filter.label}</label>
      <input id={filter.id} type='checkbox' checked={activeFilters.includes(filter.id) ? true : false} onChange={onCheckboxSelectionChange}/>
    </div>);

  return (
    <FiltersContext.Consumer>
      {props.visible ? 
        <section id='filterModal' className='fullCover'>
          <section className='modalContainer fullCover' onClick={(e) => onClickOutsideModal(e)}>
            <section className='modalBox'>
              <div className='modalTitle'>
                <h3>Filter Selection</h3>
                <div className='closeIcon' onClick={props.onClose}>
                  <i className="fas fa-times"></i>
                </div>
              </div>
              <div className='modalSelection'>
                <div className='filterSelectionGroup'>
                  <h5>Type</h5>
                  {filtersMap.TYPES.map(filter => filterConstruction(filter))}
                </div>
                <div className='filterSelectionGroup'>
                  <h5>Languages</h5>
                  {filtersMap.LANGUAGES.map(filter => filterConstruction(filter))}
                </div>
                <div className='filterSelectionGroup'>
                  <h5>Frameworks</h5>
                  {filtersMap.FRAMEWORKS.map(filter => filterConstruction(filter))}
                </div>
                <div className='filterSelectionGroup'>
                  <h5>Date</h5>
                  {filtersMap.DATES.map(filter => filterConstruction(filter))}
                </div>
              </div>
              <div className='modalConfirmation'>
                <button className='modalCancelBtn' onClick={props.onClose}>Cancel</button>
                <button type='submit' className='modalApplyBtn' onClick={applyAndClose}>Apply</button>
              </div>
            </section>
          </section>
        </section>
        :
        null}
    </FiltersContext.Consumer>
  )
}

export default FilterModal;
