import { useState, useLayoutEffect, useRef } from 'react';
import './Quote.css';

function Quote() {
  let cardRef = useRef(null);
  const [ visibility, setVisibility ] = useState(false);

  useLayoutEffect(() => {
    const handleScroll = () => {
      if(cardRef.current.getBoundingClientRect().top < 600) {
        setVisibility(true);
      }
    }
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  });

  return (
    <section id='quote-section' className={`spaced-cover section-content ${visibility ? 'visible-section' : 'hidden-section'}`}>
      <article ref={cardRef}>
        <h1 className='section-header'>Personal Statement</h1>
        <div className='quote-container'>
          <p>Hi there, thank you for visiting my website. Hope you are doing good! My name is Francisco and I am a Software Developer. I have been interested and passionate about computer science since my first year of college. I had the opportunity of working with different technologies as well as being involved in numerous projects, from academia and <i>side-jobs</i>. I have always wanted to learn more and challenge myself to acquire new and deeper skills in different areas of expertise within computer science. Machine Learning, Security, Cloud and Software Design and Development are the topics which are more appealing to me although I very much enjoy others such as Databases Design, Web Development and Cloud Technologies.</p>
          <p>My goal is to develop and offer the best possible solutions throughout my works so that its users are .</p>
          <p></p>
        </div>
        <hgroup>
          <h1><i>"Difficulties mastered</i></h1>
          <h1><i>are opportunities won"</i></h1>
          <h5>- Winston Churchill</h5>
        </hgroup>
      </article>
    </section>
  );
}

export default Quote;
