import React, { useState } from 'react';
import FilterCard from './FilterCard'
import FilterModal from './FilterModal'
import './Filter.css'

function Filter() {
  const FiltersContext = React.createContext([]);
  const [modalVisibility, setModalVisibility] = useState(false);

  const handleModalVisibility = () => {
    setModalVisibility(!modalVisibility);
  }

  const handleRemoval = (id) => {
    const updatedFilterCards = filterCards.filter(item => item.props.id !== id);
    setFilterCards(updatedFilterCards);
  }

  const handleAddition = (filterIds) => {
    let oldCards = filterCards;

    filterIds.forEach(filterId => {
      const newFilterCard = <FilterCard type='FILTER' key={filterId} id={filterId} onNewFilter={handleModalVisibility} onRemoval={handleRemoval}/>
      oldCards.unshift(newFilterCard);
    });

    setFilterCards(oldCards);
  }

  const [filterCards, setFilterCards] = useState([])

  return (
    <section id='filterSection'>
      <p>Filters:</p>
      <div id='filtersHolder'>
        <FilterCard type='ADD' key='Add' id='Add' onNewFilter={handleModalVisibility} onRemoval={handleRemoval}/>
        {filterCards}
      </div>
      <FilterModal visible={modalVisibility} onSubmit={handleAddition} onClose={handleModalVisibility}/>
    </section>
  );
}

export default Filter;
