import React, { useState } from 'react';
import FilterCard from './FilterCard'
import FilterModal from './FilterModal'
import ProjectCard from '../card/ProjectCard'
import inesctecLogo from '../../../images/companies/inesc-tec_logo.png'
import './Filter.css'

const mathex2java = <ProjectCard 
                      name='MaTheX2Java'
                      brief='Web-app focused on a free and open-source source-to-source compiler capable of efficiently translating latex mathematic equations to fully operational Java code.'
                      date='2019'
                      imgSrc={inesctecLogo}
                      alt='MaTheX2Java Logo'/>;

const researchROS = <ProjectCard 
                      name='Research Assistant'
                      brief='Study ...'
                      date='July 2019 - December 2019'
                      imgSrc={inesctecLogo}
                      alt='INESC-TEC Logo'/>;

const filtersInfoMap = {
  'c-filter'      : { label: 'C', type: 'lang', projects: [] },
  'c++-filter'    : { label: 'C++', type: 'lang', projects: [] },
  'java-filter'   : { label: 'Java', type: 'lang', projects: [mathex2java] },
  'python-filter' : { label: 'Python', type: 'lang', projects: [] },
  'react-filter'  : { label: 'React', type: 'framework', projects: [] },
  'nodejs-filter' : { label: 'NodeJS', type: 'framework', projects: [mathex2java] },
  'net-filter'    : { label: '.NET Remoting', type: 'framework', projects: [] },
  'ros-filter'    : { label: 'ROS', type: 'framework', projects: [] },
  'xamarin-filter': { label: 'Xamarin', type: 'framework', projects: [] },
  'vdm++-filter'  : { label: 'VDM++', type: 'framework', projects: [] },
  '2021-filter'   : { label: '2021', type: 'date', projects: [] },
  '2020-filter'   : { label: '2020', type: 'date', projects: [] },
  '2019-filter'   : { label: '2019', type: 'date', projects: [] },
  '2018-filter'   : { label: '2018', type: 'date', projects: [] },
  '2017-filter'   : { label: '2017', type: 'date', projects: [] },
  'ml-filter'     : { label: 'Machine Learning', type: 'type', projects: [] },
  'webapp-filter' : { label: 'Web App', type: 'type', projects: [] },
  'mobile-filter' : { label: 'Mobile', type: 'type', projects: [] },
  'game-filter'   : { label: 'Game', type: 'type', projects: [] }
}

const filtersTypesMap = {
  LANGUAGES : ['c-filter', 'c++-filter', 'java-filter', 'python-filter'],
  FRAMEWORKS: ['react-filter', 'nodejs-filter', 'net-filter', 'ros-filter', 'xamarin-filter', 'vdm++-filter'],
  DATES     : ['2021-filter', '2020-filter', '2019-filter', '2018-filter', '2017-filter'],
  TYPES     : ['ml-filter', 'webapp-filter', 'mobile-filter', 'game-filter']
};

function Filter() {
  const [ activeFilters, setActiveFilters ] = useState([])
  const [ modalVisibility, setModalVisibility ] = useState(false);
  const [ filteringProcess, setFilteringProcess ] = useState(false);

  const updateModalVisibility = () => setModalVisibility(!modalVisibility);

  const updateVisibleCards = () => {
    setFilteringProcess(true);

    setFilteringProcess(false);
  }

  const updateFilters = (filters) => {
    setActiveFilters([...filters]);
    updateVisibleCards();
  };

  const handleRemoval = (filters) => {
    let arr = activeFilters;
    setActiveFilters([...arr.filter(id => !filters.includes(id))]);
  }
  const renderLoadingPanel = () => (
    <div className='loadingPanel'>
      LOADING
    </div>
  );

  const renderCards = () => (
    <p>cards</p>
  );

  return (
    <>
      <section id='filterSection'>
        <p><strong>Filters:</strong></p>
        <div id='filtersHolder'>
          {activeFilters.map(filterId => (
            <FilterCard 
              type='FILTER' 
              key={`${filterId}-card`} 
              id={`${filterId}-card`}
              label={filtersInfoMap[filterId].label}
              onNewFilter={updateModalVisibility} 
              onRemoval={handleRemoval}
            />
          ))}
          <FilterCard 
            type='ADD' 
            key='Add' 
            id='Add' 
            onNewFilter={updateModalVisibility} 
            onRemoval={handleRemoval}
          />
        </div>
        <FilterModal
          activeFilters={activeFilters}
          filtersInfo={filtersInfoMap}
          filtersTypes={filtersTypesMap}
          visible={modalVisibility}
          onClose={updateModalVisibility}
          onSubmit={updateFilters}
        />
      </section>
      <section id='projects-contents'>
        {filteringProcess ? renderLoadingPanel() : renderCards()}
      </section>
    </>
  );
}

export default Filter;
