import { useContext, useState } from 'react';
import FiltersContext from './FilterContext';
import './FilterModal.css'

function FilterModal(props) {
  const filtersMap = props.filtersInfo;
  const filtersTypesMap = props.filtersTypes;
  const context = useContext(FiltersContext);
  const [ localFilters, setLocalFilters ] = useState(context.activeFilters);

  const applyAndClose = () => {
    context.applyFilters(localFilters);
    setLocalFilters(context.activeFilters)
    props.onClose();
  }

  const onClickOutsideModal = (e) => {
    if(e.target.classList.contains('modalContainer'))
      props.onClose();
  }

  const onCheckboxSelectionChange = (e) => {
    const selected = e.target.checked;
    const filterId = e.target.id;

    let arr = localFilters;
    if(selected) {
      arr.push(filterId);
    } else {
      arr = arr.filter(id => id !== filterId);
    }

    setLocalFilters(arr);
  }

  const filterConstruction = (id) => (
    <div className='checkboxContainer' key={`${id}-checkbox`}>
      <label className='checkboxLabel' key={`${id}-label`}>{filtersMap[id].label}</label>
      <input id={id} type='checkbox' checked={localFilters.includes(id) ? true : false} onChange={onCheckboxSelectionChange}/>
    </div>);

  return (
    <>
      {props.visible ? 
        <section id='filterModal' className='fullCover'>
          <section className='modalContainer fullCover' onClick={(e) => onClickOutsideModal(e)}>
            <section className='modalBox'>
              <div className='modalTitle'>
                <h3>Filter Selection</h3>
                <div className='closeIcon' onClick={props.onClose}>
                  <i className="fas fa-times"></i>
                </div>
              </div>
              <div className='modalSelection'>
                <div className='filterSelectionGroup'>
                  <h5>Type</h5>
                  {filtersTypesMap.TYPES.map(id => filterConstruction(id))}
                </div>
                <div className='filterSelectionGroup'>
                  <h5>Languages</h5>
                  {filtersTypesMap.LANGUAGES.map(id => filterConstruction(id))}
                </div>
                <div className='filterSelectionGroup'>
                  <h5>Frameworks</h5>
                  {filtersTypesMap.FRAMEWORKS.map(id => filterConstruction(id))}
                </div>
                <div className='filterSelectionGroup'>
                  <h5>Date</h5>
                  {filtersTypesMap.DATES.map(id => filterConstruction(id))}
                </div>
              </div>
              <div className='modalConfirmation'>
                <button className='modalCancelBtn' onClick={props.onClose}>Cancel</button>
                <button type='submit' className='modalApplyBtn' onClick={applyAndClose}>Apply</button>
              </div>
            </section>
          </section>
        </section>
        : null}
    </>
  )
}

export default FilterModal;
