import './FilterModal.css'

function FilterModal(props) {
  const languagesFilters = [
    {label: 'C'},
    {label: 'C++'},
    {label: 'Java'},
    {label: 'Python'},
  ]

  const dateFilters = [
    {label: '2021'},
    {label: '2020'},
    {label: '2019'},
    {label: '2018'},
    {label: '2017'}
  ]

  const applyAndClose = () => {
    props.onSubmit('ah');
    props.onClose();
  }

  const onClickOutside = (e) => {
    if(e.target.classList.contains('modalContainer'))
      props.onClose();
  }

  const filterConstruction = (filter) => (
    <div className='checkboxContainer'>
      <label key={filter} className='checkboxLabel'>{filter.label}</label>
      <input type='checkbox'/>
    </div>);

    /*checked={false}*/

  return (
    <>
      {props.visible ? 
        <section id='filterModal' className='fullCover'>
          <section className='modalContainer fullCover' onClick={(e) => onClickOutside(e)}>
            <section className='modalBox'>
              <div className='modalTitle'>
                <h3>Filter Selection</h3>
                <div className='closeIcon' onClick={props.onClose}>
                  <i className="fas fa-times"></i>
                </div>
              </div>
              <div className='modalSelection'>
                <div className='filterSelectionGroup'>
                  <h5>Technologies</h5>
                  {technologiesFilters.map(filter => filterConstruction(filter))}
                </div>
                <div className='filterSelectionGroup'>
                  <h5>Date</h5>
                  {dateFilters.map(filter => filterConstruction(filter))}
                </div>
              </div>
              <div className='modalConfirmation'>
                <button onClick={props.onClose}>Cancel</button>
                <button onClick={() => applyAndClose()}>Apply</button>
              </div>
            </section>
          </section>
        </section>
        :
        null}
    </>
  )
}

export default FilterModal;
