import React, { useState } from 'react';
import FilterCard from './FilterCard'
import './Filter.css'

function Filter() {
  const [modalVisibility, setModalVisibility] = useState(false);

  const handleAddition = (filter) => {
    <FilterCard type='FILTER' id={filter} onAddition={handleAddition} onRemoval={handleRemoval}/>
  }

  const handleRemoval = () => {
    console.log('removal')
  }

  const handleModalVisibility = () => {
    setModalVisibility(!modalVisibility);
  }

  const [filterCards, setFilterCards] = useState(
    [<FilterCard type='ADD' id='Add' onAddition={handleAddition} onRemoval={handleRemoval}/>])

  return (
    <section id='filterSection'>
      <p>Filters:</p>
      <div id='filtersHolder'>
        {filterCards}
      </div>
    </section>
  );
}

export default Filter;
