import React, { useState } from 'react';
import FilterCard from './FilterCard'
import FilterModal from './FilterModal'
import './Filter.css'

function Filter() {
  const [modalVisibility, setModalVisibility] = useState(false);

  const handleAddition = (filter) => {
    <FilterCard type='FILTER' id={filter} onAddition={handleModalVisibility} onRemoval={handleRemoval}/>
  }

  const handleRemoval = () => {
    console.log('removal')
  }

  const handleModalVisibility = () => {
    setModalVisibility(!modalVisibility);
    console.log(modalVisibility)
  }

  const [filterCards, setFilterCards] = useState(
    [<FilterCard type='ADD' id='Add' onAddition={handleModalVisibility} onRemoval={handleRemoval}/>])

  return (
    <section id='filterSection'>
      <p>Filters:</p>
      <div id='filtersHolder'>
        {filterCards}
      </div>
      {modalVisibility ? <p>MODAL</p> : null}
    </section>
  );
}

export default Filter;
