import { useState } from 'react';
import Navbar from './Navbar';
import './Menu.css'

function Menu() {
  const [ visibility, setVisibility ] = useState(false);

  const updateMenuVisibility = () => {
    setVisibility(!visibility);
  }

  const onBackgroundClick = (e) => {
    if(e.target.classList.contains('menuBackground'))
      updateMenuVisibility();
  }

  return (
    <>
      <Navbar onDrawerClick={updateMenuVisibility}/>
      {visibility ? 
        <section className='menuBackground fullCover' onClick={(e) => onBackgroundClick(e)}>
          <section id='menu'>
            <header>
              Table of Contents
              <div onClick={updateMenuVisibility}><i class="fas fa-times"></i></div>
            </header>

            <ul>
              <li>Introduction</li>
              <li>About</li>
              <li>Experience</li>
              <li>Portfolio</li>
              <li>Contact</li>
            </ul>
            <footer>
              <a href="#">GITHUB</a>
              <a href="#">LINKEDIN</a>
              <a href="#">MAIL</a>
            </footer>
          </section>
        </section> : null}
    </>
  )
}

export default Menu;
