import { useState } from 'react'
import './Menu.css'

function Menu() {
  const [ visibility, setVisibility ] = useState(false);

  const onBackgroundClick = (e) => {
    if(e.target.classList.contains('menuBackground'))
      setVisibility(!visibility);
  }

  return (
    <>
      <Navbar />
      {visibility ? 
        <section className='menuBackground fullCover' onClick={(e) => onBackgroundClick(e)}>
          <section id='menu'>
            <header>Table of Contents</header>
            <ul>
              <li>Introduction</li>
              <li>About</li>
              <li>Experience</li>
              <li>Portfolio</li>
              <li>Contact</li>
            </ul>
            <footer>
              <a href="#">GITHUB</a>
              <a href="#">LINKEDIN</a>
              <a href="#">MAIL</a>
            </footer>
          </section>
        </section> : null}
    </>
  )
}

export default Menu;
