import './Quote.css';

function Quote() {
  let cardRef = useRef(null);
  const [ visibility, setVisibility ] = useState(false);

  useLayoutEffect(() => {
    const handleScroll = () => {
      if(window.scrollY > cardRef.current.offsetHeight) {
        setVisibility(true);
      }
    }
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  });

  return (
    <section id='quote-section' className={`full-cover centered-content ${visibility ? 'visible-section' : 'hidden-section'}`}>
