import React, { useState } from 'react';
import FilterCard from './FilterCard'
import FilterModal from './FilterModal'
import FiltersContext from './FilterContext';
import './Filter.css'

const filtersMap = {
  LANGUAGES: [
    { id: 'c-filter', label: 'C' },
    { id: 'c++-filter', label: 'C++' },
    { id: 'java-filter', label: 'Java' },
    { id: 'python-filter',label: 'Python' }
  ],
  FRAMEWORKS: [
    { id: 'react-filter', label: 'React' },
    { id: 'nodejs-filter', label: 'NodeJS' },
    { id: 'net-filter', label: '.NET Remoting' },
    { id: 'ros-filter', label: 'ROS' },
    { id: 'xamarin-filter', label: 'Xamarin' },
    { id: 'vdm++-filter', label: 'VDM++' }
  ],
  DATES: [
    { id: '2021-filter', label: '2021' },
    { id: '2020-filter', label: '2020' },
    { id: '2019-filter', label: '2019' },
    { id: '2018-filter', label: '2018' },
    { id: '2017-filter', label: '2017' }
  ],
  TYPES: [
    { id: 'ml-filter', label: 'Machine Learning' } ,
    { id: 'webapp-filter', label: 'Web App' },
    { id: 'mobile-filter', label: 'Mobile' },
    { id: 'game-filter', label: 'Game' }
  ]
};

function Filter() {
  const [ activeFilters, setActiveFilters ] = useState([])
  const [ modalVisibility, setModalVisibility ] = useState(false);

  const updateModalVisibility = () => {
    setModalVisibility(!modalVisibility);
  }

  const handleRemoval = (ids) => {
    let arr = activeFilters;
    arr.filter(item => !ids.includes(item))
    setActiveFilters(arr);
  }

  const handleAddition = (ids) => {
    let arr = activeFilters;
    setActiveFilters(arr.concat(ids));
  }

  const submit = () => {

  }

  return (
    <FiltersContext.Provider value={{ activeFilters: activeFilters, removeFilters: handleRemoval, addFilters: handleAddition }}>
      <section id='filterSection'>
        <p>Filters:</p>
        <div id='filtersHolder'>
          <FilterCard type='ADD' key='Add' id='Add' onNewFilter={updateModalVisibility} onRemoval={handleRemoval}/>
          {activeFilters.map(filter => (
            <FilterCard type='FILTER' key={filter} id={filter} onNewFilter={updateModalVisibility} onRemoval={handleRemoval}/>
          ))}
        </div>
        <FilterModal
          filters={filtersMap}
          visible={modalVisibility}
          onClose={updateModalVisibility}
          onSubmit={submit}
        />
      </section>
    </FiltersContext.Provider>
  );
}

export default Filter;
