import React, { useState } from 'react';
import FilterCard from './FilterCard'
import FilterModal from './FilterModal'
import './Filter.css'

function Filter() {
  const [modalVisibility, setModalVisibility] = useState(false);

  const handleModalVisibility = () => {
    setModalVisibility(!modalVisibility);
    console.log(modalVisibility)
  }

  const handleRemoval = (id) => {
    const updatedFilterCards = filterCards.filter(item => item.props.id !== id);
    setFilterCards(updatedFilterCards);
  }

  const handleAddition = (filterId) => {
    <FilterCard type='FILTER' id={filterId} onNewFilter={handleModalVisibility} onRemoval={handleRemoval}/>
  }

  const [filterCards, setFilterCards] = useState(
    [<FilterCard type='FILTER' id='ah' onNewFilter={handleModalVisibility} onRemoval={handleRemoval}/>,
    <FilterCard type='ADD' id='Add' onNewFilter={handleModalVisibility} onRemoval={handleRemoval}/>])

  return (
    <section id='filterSection'>
      <p>Filters:</p>
      <div id='filtersHolder'>
        {filterCards}
      </div>
      <FilterModal visible={modalVisibility} onSubmit={handleAddition} />
    </section>
  );
}

export default Filter;
